import { HttpClient } from '@angular/common/http';
import { Component, OnInit } from '@angular/core';

@Component({
  selector: 'app-persondata',
  templateUrl: './persondata.component.html',
  styleUrls: ['./persondata.component.css']
})
export class PersondataComponent implements OnInit {


    static myname ='hello';

  constructor(private http: HttpClient ) { }


  employees: any[] = [];

  ngOnInit(): void {
    this.getEmployees();
  }

  getEmployees() {

    this.http.get<any[]>('http://localhost:8080/getemployeesdata').subscribe(
      (data:any) => {
        this.employees = data;
        console.log(data);


      },
      (error) => {
        console.error('Error fetching employee data:', error);
      }
    );
  }




}


  <button [routerLink]="['/register']">Back</button>

<div *ngFor="let employee of employees" style="border: 1px solid black; padding: 10px;">
  <p style="font-weight: bold;">Name: {{ employee.FullName }}</p>
  <p style="font-style: italic;">Gender: {{ employee.Gender }}</p>
  <p style="color: red;">Fromdate: {{ employee.Leave_date }}</p>
  <p style="color: blue;">Todate: {{ employee.Leave_duration }}</p>
  <p style="font-size: 18px;">Phone: {{ employee.Leave_type }}</p>
  <p style="text-align: center;">Resume: <a href="http://localhost:8080/{{ employee.Resume }} "  > Download</a> </p>
  <p style="text-decoration: underline;">Email: {{ employee.Team_name }}</p>



  <!-- <hr> -->
</div>
