import { HttpClient } from '@angular/common/http';
import { Component } from '@angular/core';

@Component({
  selector: 'app-kpi3',
  templateUrl: './kpi3.component.html',
  styleUrls: ['./kpi3.component.css']
})
export class Kpi3Component {






  constructor(private http: HttpClient ) { }


  kpi3data: any[] = [];

  ngOnInit(): void {
    this.getEmployees();
  }

  getEmployees() {

    this.http.get<any[]>('http://localhost:8080/getkpi3').subscribe(
      (data:any) => {
        this.kpi3data = data;
        console.log(data);


      },
      (error) => {
        console.error('Error fetching employee data:', error);
      }
    );
  }

}


<button [routerLink]="['/register']">Back</button>

<div *ngFor="let employee of kpi3data" style="border: 1px solid black; padding: 10px;">

  <!-- <p style="color: red;">Fromdate: {{ employee.FullName }}</p>
  <p style="color: blue;">Todate: {{employee.Leave_duration }}</p>
   -->


   <table>
    <thead>
      <tr>
        <th>Full name</th>
        <th></th>
      </tr>
    </thead>
    <tbody>
      <tr>
        <td>{{ employee.FullName }}</td>
        <td>{{ employee.Leave_duration }}</td>
      </tr>
    </tbody>
  </table>


</div>
