import { HttpClient } from '@angular/common/http';
import { Component, OnInit } from '@angular/core';
import { Router, RouterLink } from '@angular/router';
import { Chart, ChartOptions, ChartType } from 'chart.js';
import { registerables } from 'chart.js';
Chart.register(...registerables);
@Component({
  selector: 'app-kpi4',
  templateUrl: './kpi4.component.html',
  styleUrls: ['./kpi4.component.css']
})
export class Kpi4Component implements OnInit {

constructor(private http: HttpClient,private router:Router ) { }


kpi4data: any[] = [];

ngOnInit(): void {
  this.getEmployees();
}

getEmployees() {

  this.http.get<any[]>('http://localhost:8080/getkpi4').subscribe(
    (data:any) => {
      this.kpi4data = data;
      this.myChart();
      console.log(data);


    },
    (error) => {
      console.error('Error fetching employee data:', error);
    }
  );
}



labels = this.kpi4data.map((item: any) => item.label);
values = this.kpi4data.map((item: any) => item.value);

 myChart()
 { new Chart("myChart", {



  type: 'bar',
  data: {
      labels: this.labels,
      datasets: [{
          label: '# of Votes',
          data: this.values,
          backgroundColor: [
              'rgba(255, 99, 132, 0.2)',
              'rgba(54, 162, 235, 0.2)',
              'rgba(255, 206, 86, 0.2)',
              'rgba(75, 192, 192, 0.2)',
              'rgba(153, 102, 255, 0.2)',
              'rgba(255, 159, 64, 0.2)'
          ],
          borderColor: [
              'rgba(255, 99, 132, 1)',
              'rgba(54, 162, 235, 1)',
              'rgba(255, 206, 86, 1)',
              'rgba(75, 192, 192, 1)',
              'rgba(153, 102, 255, 1)',
              'rgba(255, 159, 64, 1)'
          ],
          borderWidth: 1
      }]
  },
  options: {
      scales: {
          y: {
              beginAtZero: true
          }
      }
  }
});
 }

