}
<button [routerLink]="['/register']">Back</button>
<div *ngFor="let employee of kpi4data" style="border: 1px solid black; padding: 10px;">

    <labels  style="font-weight: bold;">Name: {{ employee.FullName }}</labels>
    <data style="font-style: italic;">Gender: {{ employee.Leave_duration }}</data>

</div>


