import { HttpClient } from '@angular/common/http';
import { Component, OnInit } from '@angular/core';
import { Router, RouterLink } from '@angular/router';
import { Chart, ChartOptions, ChartType } from 'chart.js';

@Component({
  selector: 'app-kpi4',
  templateUrl: './kpi4.component.html',
  styleUrls: ['./kpi4.component.css']
})
export class Kpi4Component implements OnInit {

  public chart: any;

constructor(private http: HttpClient,private router:Router ) { }


kpi4data: any[] = [];

ngOnInit(): void {
  this.getEmployees();
}

getEmployees() {

  this.http.get<any[]>('http://localhost:8080/getkpi4').subscribe(
    (data:any) => {
      this.kpi4data = data;
      console.log(data);


    },
    (error) => {
      console.error('Error fetching employee data:', error);
    }
  );
}

}
<button [routerLink]="['/register']">Back</button>
 <div *ngFor="let employee of kpi4data" style="border: 1px solid black; padding: 10px;">

    <p  style="font-weight: bold;">Name: {{ employee.FullName }}</p>
    <p style="font-style: italic;">Gender: {{ employee.Leave_duration }}</p>
