import { HttpClient } from '@angular/common/http';
import { Component, OnInit } from '@angular/core';
import { Chart, ChartOptions, ChartType } from 'chart.js';

@Component({
  selector: 'app-kpi4',
  templateUrl: './kpi4.component.html',
  styleUrls: ['./kpi4.component.css']
})
export class Kpi4Component implements OnInit {

  public chart: any;
  tableData: boolean = true;
  tableBtn: boolean = true;
  router: any;

  createChart(){





    this.tableData = false;
    this.tableBtn = false;
    this.router.navigateByUrl('/kpi4');



    this.chart = new Chart("MyChart", {
      type: 'bar', //this denotes tha type of chart

      data: {// values on X-Axis
        labels: ['Alex Gonzalez', 'Barbara Olson', 'Benjamin Jensen','2022-05-13',
								 '2022-05-14', '2022-05-15', '2022-05-16','2022-05-17', ], 
	       datasets: [
          {
            label: "Sales",
            data: ['467','576', '572', '79', '92',
								 '574', '573', '576'],
            backgroundColor: 'blue'
          },
          {
            label: "Profit",
            data: ['542', '542', '536', '327', '17',
									 '0.00', '538', '541'],
            backgroundColor: 'limegreen'
          }  
        ]
      },
      options: {
        aspectRatio:2.5
      }

    });


}



constructor(private http: HttpClient ) { }


kpi4data: any[] = [];

ngOnInit(): void {
  this.getEmployees();
}

getEmployees() {

  this.http.get<any[]>('http://localhost:8080/getkpi4').subscribe(
    (data:any) => {
      this.kpi4data = data;
      console.log(data);


    },
    (error) => {
      console.error('Error fetching employee data:', error);
    }
  );
}

}
